import React, { useState } from 'react';


const DescriptiveSlider = ({ value, onChange, levels }) => {
  const getBackgroundSize = () => {
    return { backgroundSize: `${(value * 100) / (levels.length - 1)}% 100%` }
  };

  return (
    <div className="relative pt-1">
      <input
        type="range"
        min="0"
        max={levels.length - 1}
        value={value}
        onChange={onChange}
        step="1"
        className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
        style={{
          ...getBackgroundSize(),
          background: `linear-gradient(to right, #4CAF50 0%, #4CAF50 ${(value * 100) / (levels.length - 1)}%, #ddd ${(value * 100) / (levels.length - 1)}%, #ddd 100%)`
        }}
      />
      <ul className="flex justify-between w-full px-[10px] mt-4">
        {levels.map((level, index) => (
          <li key={index} className={`flex justify-center relative ${index === value ? 'font-bold text-green-600' : ''}`} style={{width: `${100 / (levels.length - 1)}%`}}>
            <span className="absolute -top-6 transform -translate-x-1/2 bg-white px-2 py-1 rounded shadow text-xs">
              {level}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

const ITPlanner = () => {
  const [networkCapability, setNetworkCapability] = useState(2);
  const networkLevels = [
    "Basic\n(Switches)",
    "Standard\n(Switches, Routers)",
    "Advanced\n(Switches, Routers, Firewall)",
    "Premium\n(Switches, Routers, Firewall, 24/7 Support)",
    "Enterprise\n(Custom Solution)"
  ];

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-xl">
      <h1 className="text-2xl font-bold mb-6 text-center">IT Infrastructure Planner</h1>
      
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Network Capability</h2>
        <DescriptiveSlider
          value={networkCapability}
          onChange={(e) => setNetworkCapability(parseInt(e.target.value))}
          levels={networkLevels}
        />
      </div>

      <div className="mt-6 p-4 bg-gray-100 rounded-lg">
        <h3 className="text-lg font-semibold mb-2">Selected Network Capability:</h3>
        <p className="text-gray-700">{networkLevels[networkCapability]}</p>
      </div>

      {/* Placeholder for other sliders and components */}
      <div className="mt-6 text-center text-gray-500">
        (Other infrastructure components would be implemented similarly)
      </div>
    </div>
  );
};

export default ITPlanner;
